import "./SideCart.css";

//components
import CartItem from './CartItem';

const SideCart = ({show}) => {
    const sideCartClass = ["sideCart"];

    if (show) {
        sideCartClass.push("appear");
    };

    return <div className={sideCartClass}>
        <div className="sideCart__left">
            <h2>Shopping cart</h2>

            <div className="cartItem">
                CartItem
            </div>
        </div>
        <div className="sideCart__right">
            
        </div>
    </div>
};

export default SideCart;
