import './App.css';
import{BrowserRouter as Router, Switch, Route} from "react-router-dom";

function App() {
  return (
    <div className="app">
      {/* Navbar */}
      {/* SideDrawer */}
      {/* Backdrop */}
      <main>
        <Switch>
          <Route exact path="/" component= {Home}/>
          <Route exact path="/product/:id" component= {ProductScreen}/>
          <Route exact path="cart" component= {CartScreen}/>
        </Switch>
      </main>
      {/* HomeScreen */}
      {/* ProductScreen */}
      {/* CartScreen */}
    </div>
  );
}

export default App;
