import "./SideCart.css";

//components
import CartItem from './CartItem';

const SideCart = (show) => {
    const sideCartClass = ["sideCart"];

    if(show) {
        sideCartClass.push("show")
    }

    return <div className={sideCartClass.join(" ")}>
        <div className="sideCart__left">
            <h2>Shopping cart</h2>

            <CartItem />
        </div>
        <div className="sideCart__right">
            
        </div>
    </div>
};

export default SideCart;
