import "./SideCart.css";

//components
import CartItem from './CartItem';

const SideCart = () => {
    const cart__link = document.getElementById('cart__link')
    const Cartitem = document.getElementById('cartitem');

    cart__link.addEventListener('click', () => {
        Cartitem.classList.toggle('show');
    });

    return <div className="sideCart">
        <div className="sideCart__left">
            <h2>Shopping cart</h2>

            <CartItem />
        </div>
        <div className="sideCart__right">
            
        </div>
    </div>
};

export default SideCart;
